import React, { useState, useEffect } from 'react';
import { Amadeus } from 'amadeus';

const amadeus = new Amadeus({
  clientId: 'xge4TD4YcAJXPNAJNlY2lscGJQySEQIi',
  clientSecret: 'vePijExjyQvNFQsB',
});
const BrowsePage = () => {
  const [flights, setFlights] = useState([]);
  const [bookedFlights, setBookedFlights] = useState([]);
  const [cancelledFlights, setCancelledFlights] = useState([]);
  const [rescheduledFlights, setRescheduledFlights] = useState([]);

  // Fetch flights from Amadeus API
  useEffect(() => {
    amadeus.shopping.flightOffers.get().then(response => {
      setFlights(response.data);
    }).catch(error => {
      console.log(error);
    });
  }, []);

  // Handle booking a flight
  const handleBookFlight = (flight) => {
    // TODO: Implement booking logic
    const newBookedFlights = [...bookedFlights, flight];
    setBookedFlights(newBookedFlights);
  }

  // Handle cancelling a flight
  const handleCancelFlight = (flight) => {
    // TODO: Implement cancellation logic
    const newCancelledFlights = [...cancelledFlights, flight];
    setCancelledFlights(newCancelledFlights);
  }

  // Handle rescheduling a flight
  const handleRescheduleFlight = (flight) => {
    // TODO: Implement rescheduling logic
    const newRescheduledFlights = [...rescheduledFlights, flight];
    setRescheduledFlights(newRescheduledFlights);
  }

  return (
    <div>
      <h1>Browse Flights</h1>
      <ul>
        {flights.map(flight => (
          <li key={flight.id}>
            <h2>{flight.offerItems[0].price.total} {flight.offerItems[0].price.currency}</h2>
            <p>{flight.offerItems[0].services[0].segments[0].flightSegment.carrierCode} {flight.offerItems[0].services[0].segments[0].flightSegment.number}</p>
            <button onClick={() => handleBookFlight(flight)}>Book</button>
            <button onClick={() => handleCancelFlight(flight)}>Cancel</button>
            <button onClick={() => handleRescheduleFlight(flight)}>Reschedule</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
export default BrowsePage

