import React, { useState } from "react";

const Flight = ({ airline, departureTime, departureGateNumber, boardingLocation ,price }) => {
  const [isBooked, setIsBooked] = useState(false);
  const [isCancelled, setIsCancelled] = useState(false);
  const [isRescheduled, setIsRescheduled] = useState(false);
  const [bookedDate, setBookedDate] = useState("");
  const [cancelledDate, setCancelledDate] = useState("");
  const [rescheduledDate, setRescheduledDate] = useState("");
  const [selectedPaymentOption, setSelectedPaymentOption] = useState("");
  const [paymentNumber, setPaymentNumber] = useState("");

  const mpesaPromptStyle = {
    color: 'blue',
    fontSize: '16px',
    fontWeight: 'bold',
    textAlign: 'center',
  };

  const handleBook = () => {
    if (selectedPaymentOption === "") {
      alert("Please select a payment option.");
      return;
    }

    if (selectedPaymentOption === "M-Pesa") {
      if (paymentNumber === "") {
        alert("Please enter your M-Pesa number.");
        return;
      }
      const mpesaPrompt = <p>`A Ussd will be sent to your number {paymentNumber} 
      to confirm payment of KES {price}.
       Or lipa na Mpesa, Paybill 1111 account number KQ`; </p>
      if (window.confirm(mpesaPrompt)) {
        setIsBooked(true);
        setBookedDate(new Date().toLocaleString());
      }
    } else {
      if (paymentNumber === "") {
        alert(`Please enter your ${selectedPaymentOption} number.`);
        return;
      }
      const paymentPrompt = `Please pay $${price} using ${selectedPaymentOption} number ${paymentNumber}.`;
      if (window.confirm(paymentPrompt)) {
        setIsBooked(true);
        setBookedDate(new Date().toLocaleString());
      }
    }
  };

  const handleCancel = () => {
    setIsCancelled(true);
    setCancelledDate(new Date().toLocaleString());
  };

  const handleReschedule = () => {
    setIsRescheduled(true);
    setRescheduledDate(new Date().toLocaleString());
  };

  const handlePaymentOptionChange = (event) => {
    setSelectedPaymentOption(event.target.value);
  };

  const handlePaymentNumberChange = (event) => {
    setPaymentNumber(event.target.value);
  };

  return (
    <div>
      <div className="flight">
        <div className="fligh">
          <h2 style={{ color: "#003459", borderBottom: "black" }}>{airline}</h2>
          <p style={{ color: "red" }}>Departure Time: {departureTime}</p>
          <p style={{ color: "black" }}>Boarding Location: {boardingLocation}</p>
          <p style={{ color: "#244D61" }}>Gate of Departure: {departureGateNumber}</p>
          <p>Price: {price}</p>
        </div>
        {isBooked ? (
          <p>
            Booked on {bookedDate}.{" "}
            <button className="flg" onClick={handleCancel}>
              Cancel
            </button>{" "}
            <button className="flg" onClick={handleReschedule}>
              Reschedule
            </button>
          </p>
        ) : (
          <div>
            <p>Select Payment Option:</p>
            <label>
              <input
                type="radio"
                value="Credit Card"
                checked={selectedPaymentOption === "Credit Card"}
                onChange={handlePaymentOptionChange}
              />
              Credit Card
            </label>
            <label>
              <input
                type="radio"
                value="Paypal"
                checked={selectedPaymentOption === "Paypal"}
                onChange={handlePaymentOptionChange}
                />
                Paypal
                </label>
                <label>
                <input
                type="radio"
                value="M-Pesa"
                checked={selectedPaymentOption === "M-Pesa"}
                onChange={handlePaymentOptionChange}
                />
                M-Pesa
                </label>
                {selectedPaymentOption === "M-Pesa" ? (
                <div>
                <label>
                M-Pesa Number:
                <input
                                 type="text"
                                 value={paymentNumber}
                                 onChange={handlePaymentNumberChange}
                               />
                </label>
                </div>
                ) : (
                <div>
                <label>
                {selectedPaymentOption} Number:
                <input
                                 type="text"
                                 value={paymentNumber}
                                 onChange={handlePaymentNumberChange}
                               />
                </label>
                </div>
                )}
                <button className="flg" onClick={handleBook}>
                Book
                </button>
                </div>
                )}
                </div>
                {isCancelled && (
                <p>
                Cancelled on {cancelledDate}.{" "}
                <button className="flg" onClick={() => setIsCancelled(false)}>
                Undo
                </button>
                </p>
                )}
                {isRescheduled && (
                <p>
                Rescheduled on {rescheduledDate}.{" "}
                <button className="flg" onClick={() => setIsRescheduled(false)}>
                Undo
                </button>
                </p>
                )}
                </div>
                );
                };

                export default Flight;
