import React, { useState } from 'react';
import { Table, Button } from 'react-bootstrap';

const Admindash = (props) => {
  const { flights } = props;
  const [rescheduledFlight, setRescheduledFlight] = useState(null);

  const getUniqueFlightName = (airlineName) => {
    // Replace spaces with hyphens and append a random number
    const randomNum = Math.floor(Math.random() * 10000);
    const uniqueName = airlineName.replace(/ /g, '-') + '-' + randomNum;

    return uniqueName;
  };

  const handleReschedule = (index) => {
    setRescheduledFlight(index);
  };

  return (
    <div className="Admidash">
      <h2>Available flights today</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Airline</th>
            <th>Departure Time</th>
            <th>Departure Gate Number</th>
            <th>Price</th>
            <th>Reschedule</th>
          </tr>
        </thead>
        <tbody>
          {flights.map((flight, index) => (
            <tr key={index} style={{ backgroundColor: index === rescheduledFlight ? 'blue' : 'transparent' }}>
              <td>{getUniqueFlightName(flight.airline)}</td>
              <td>{flight.departureTime}</td>
              <td>{flight.departureGateNumber}</td>
              <td>{flight.price}</td>
              <td>
                <Button onClick={() => handleReschedule(index)}>Reschedule</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default Admindash;
