import { Link } from "react-router-dom";
import React, { useState } from "react";
import "./BrowsePage.css"

const BrowsePage = () => {
  const [paymentMethod, setPaymentMethod] = useState("");
  const [flights, setFlights] = useState([
    {
      airline: "Ethiopian Airlines",
      departureTime: "8:00 PM",
      departureGateNumber: "14",
      price: "$100",
      isBooked: false,
      isCancelled: false,
      isRescheduled: false,
      bookedDate: "",
      cancelledDate: "",
      rescheduledDate: "",
    },{
      airline: "Ethiopian Airlines",
      departureTime: "5:00 PM",
      departureGateNumber: "1",
      price: "$150",
      isBooked: false,
      isCancelled: false,
      isRescheduled: false,
      bookedDate: "",
      cancelledDate: "",
      rescheduledDate: "",
    },
    {
      airline: "Ethiopian Airlines",
      departureTime: "8:00 PM",
      departureGateNumber: "14",
      price: "$100",
      isBooked: false,
      isCancelled: false,
      isRescheduled: false,
      bookedDate: "",
      cancelledDate: "",
      rescheduledDate: "",
    },{
      airline: "Ethiopian Airlines",
      departureTime: "5:00 PM",
      departureGateNumber: "1",
      price: "$150",
      isBooked: false,
      isCancelled: false,
      isRescheduled: false,
      bookedDate: "",
      cancelledDate: "",
      rescheduledDate: "",
    },
  {
      airline: "Ethiopian Airlines",
      departureTime: "5:00 PM",
      departureGateNumber: "1",
      price: "$150",
      isBooked: false,
      isCancelled: false,
      isRescheduled: false,
      bookedDate: "",
      cancelledDate: "",
      rescheduledDate: "",
    },

    {
      airline: "Ethiopian Airlines",
      departureTime: "8:00 PM",
      departureGateNumber: "14",
      price: "$100",
      isBooked: false,
      isCancelled: false,
      isRescheduled: false,
      bookedDate: "",
      cancelledDate: "",
      rescheduledDate: "",
    },{
      airline: "Ethiopian Airlines",
      departureTime: "5:00 PM",
      departureGateNumber: "1",
      price: "$150",
      isBooked: false,
      isCancelled: false,
      isRescheduled: false,
      bookedDate: "",
      cancelledDate: "",
      rescheduledDate: "",
    },
    {
      airline: "Ethiopian Airlines",
      departureTime: "8:00 PM",
      departureGateNumber: "14",
      price: "$100",
      isBooked: false,
      isCancelled: false,
      isRescheduled: false,
      bookedDate: "",
      cancelledDate: "",
      rescheduledDate: "",
    },
  ]);

  const handleBook = (index) => {
    if (paymentMethod === "") {
      alert("Please select a payment method.");
      return;
    }
    const newFlights = [...flights];
    newFlights[index].isBooked = true;
    newFlights[index].bookedDate = new Date().toLocaleString();
    setFlights(newFlights);
  };

  const handleCancel = (index) => {
    const newFlights = [...flights];
    newFlights[index].isCancelled = true;
    newFlights[index].cancelledDate = new Date().toLocaleString();
    setFlights(newFlights);
    };

    const handleReschedule = (index) => {
    const newFlights = [...flights];
    newFlights[index].isRescheduled = true;
    newFlights[index].rescheduledDate = new Date().toLocaleString();
    setFlights(newFlights);
    };

    const handlePaymentMethodChange = (event) => {
    setPaymentMethod(event.target.value);
    };

    return (
    <div className="browse-page">
    <h1>Browse Flights</h1>
    <div className="payment-method">
    <label htmlFor="payment-method-select">Payment Method:</label>
    <select id="payment-method-select" onChange={handlePaymentMethodChange}>
    <option value="">Select a payment method</option>
    <option value="credit-card">Credit Card</option>
    <option value="paypal">PayPal</option>
    <option value="apple-pay">Apple Pay</option>
    </select>
    </div>
    <div className="flights-list">
    {flights.map((flight, index) => (
    <div key={index} className="flight">
    <div className="flight-details">
    <div>
    <strong>Airline:</strong> {flight.airline}
    </div>
    <div>
    <strong>Departure Time:</strong> {flight.departureTime}
    </div>
    <div>
    <strong>Departure Gate Number:</strong> {flight.departureGateNumber}
    </div>
    <div>
    <strong>Price:</strong> {flight.price}
    </div>
    </div>
    <div className="flight-actions">
    {!flight.isBooked && (
    <button onClick={() => handleBook(index)}>Book</button>
    )}
    {flight.isBooked && !flight.isCancelled && (
    <button onClick={() => handleCancel(index)}>Cancel</button>
    )}
    {flight.isBooked && !flight.isCancelled && !flight.isRescheduled && (
    <button onClick={() => handleReschedule(index)}>Reschedule</button>
    )}
    </div>
    </div>
    ))}
    </div>
    <Link to="/">Back to Home</Link>
    </div>
    );
    };

    export default BrowsePage;
