import React, { useState } from "react";


const Flight = ({ airline, departureTime, departureGateNumber, price }) => {
  const [isBooked, setIsBooked] = useState(false);
  const [isCancelled, setIsCancelled] = useState(false);
  const [isRescheduled, setIsRescheduled] = useState(false);
  const [bookedDate, setBookedDate] = useState("");
  const [cancelledDate, setCancelledDate] = useState("");
  const [rescheduledDate, setRescheduledDate] = useState("");
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState("");

  const handleBook = () => {
    if (selectedPaymentMethod === "") {
      alert("Please select a payment method");
      return;
    }
    setIsBooked(true);
    setBookedDate(new Date().toLocaleString());
  };

  const handleCancel = () => {
    setIsCancelled(true);
    setCancelledDate(new Date().toLocaleString());
  };

  const handleReschedule = () => {
    setIsRescheduled(true);
    setRescheduledDate(new Date().toLocaleString());
  };

  const handlePaymentMethodChange = (event) => {
    setSelectedPaymentMethod(event.target.value);
  };

  return (
    <div>
      <div className="flight">
        <div className="fligh">
          <h2 style={{ color: "#003459", borderBottom: "black" }}>{airline}</h2>
          <p style={{ color: "red" }}>Departure Time: {departureTime}</p>

          <p style={{ color: "#244D61" }}>Gate of Departure: {departureGateNumber}</p>
          <p>Price: {price}</p>
        </div>
        {!isBooked && (
          <div>
            <p>Select a payment method:</p>
            <div >
              <input
                type="radio"
                id="mastercard"
                name="paymentMethod"
                value="mastercard"
                onChange={handlePaymentMethodChange}
              />
              <label htmlFor="mastercard">Master Card</label>
            </div>
            <div>
              <input
                type="radio"
                id="paypal"
                name="paymentMethod"
                value="paypal"
                onChange={handlePaymentMethodChange}
              />
              <label htmlFor="paypal">Paypal</label>
            </div>
            <div>
              <input
                type="radio"
                id="mpesa"
                name="paymentMethod"
                value="mpesa"
                onChange={handlePaymentMethodChange}
              />
              <label htmlFor="mpesa">Mpesa</label>
            </div>
            <div>
              <input
                type="radio"
                id="payoneer"
                name="paymentMethod"
                value="payoneer"
                onChange={handlePaymentMethodChange}
              />
              <label htmlFor="payoneer">Payoneer</label>
            </div>
            <button className="flig" onClick={handleBook}>
              Book Now
            </button>
          </div>
        )}
        {isBooked && (
          <p>
            Booked on {bookedDate}.{" "}
            <button className="flg" onClick={handleCancel}>
              Cancel
            </button>{" "}
            <button className="flg" onClick={handleReschedule}>
              Reschedule
            </button>
          </p>
        )}
      {isCancelled && <p style={{color: "red", fontStyle: 'italic'}}>Cancelled on {cancelledDate}.</p>}
      {isRescheduled && <p style={{color: "#244D61", marginBottom: '20px', fontStyle: 'bold'}}>Rescheduled on {rescheduledDate}.</p>}
    </div>

    </div>
  );
};

export default Flight;
