import React, { useState } from "react";
import { Link } from "react-router-dom";

const FlightTracker = () => {
  const [bookedFlights, setBookedFlights] = useState([]);
  const [cancelledFlights, setCancelledFlights] = useState([]);
  const [rescheduledFlights, setRescheduledFlights] = useState([]);

  const cancelFlight = (airline, departureTime) => {
    setBookedFlights((prevFlights) =>
      prevFlights.filter(
        (flight) =>
          flight.airline !== airline || flight.departureTime !== departureTime
      )
    );
    setCancelledFlights([
      ...cancelledFlights,
      { airline, departureTime, date: new Date().toLocaleDateString() },
    ]);
  };

  const rescheduleFlight = (airline, departureTime) => {
    setBookedFlights((prevFlights) =>
      prevFlights.filter(
        (flight) =>
          flight.airline !== airline || flight.departureTime !== departureTime
      )
    );
    setRescheduledFlights([
      ...rescheduledFlights,
      { airline, departureTime, date: new Date().toLocaleDateString() },
    ]);
  };

  return (
    <>
    <Link  to="/browse-page" className="fiig">Go back</Link>
    <div className="flight-tracker">
      <h2>Flight Tracker</h2>
      <div className="booked-flights">
        <h3>Booked Flights</h3>
        <ul>
          {bookedFlights.map((flight, index) => (
            <li key={index}>
              {flight.airline} - {flight.departureTime}{" "}
              <button onClick={() => cancelFlight(flight.airline, flight.departureTime)}>Cancel</button>
              <button onClick={() => rescheduleFlight(flight.airline, flight.departureTime)}>Reschedule</button>
            </li>
          ))}
        </ul>
      </div>
      <div className="cancelled-flights">
        <h3>Cancelled Flights</h3>
        <ul>
          {cancelledFlights.map((flight, index) => (
            <li key={index}>
              {flight.airline} - {flight.departureTime} ({flight.date})
            </li>
          ))}
        </ul>
      </div>
      <div className="rescheduled-flights">
        <h3>Rescheduled Flights</h3>
        <ul>
          {rescheduledFlights.map((flight, index) => (
            <li key={index}>
              {flight.airline} - {flight.departureTime} ({flight.date})
            </li>
          ))}
        </ul>
      </div>
    </div>
    </>
  );
};

export default FlightTracker;
