import { BrowserRouter,Router, Route } from "react-router-dom";

import React, { useState, useEffect } from 'react';
import { lazy } from "react";
// import { Suspense } from "react";
// import Spinner from "./../Layout/Spinner";
import { Navigate } from "react-router-dom";
import FlightTracker from "../components/FlightTracker";
import Auth from "../components/Auth";
import Admindash from "../components/Admindash";
import "bootstrap/dist/css/bootstrap.min.css"
// import usersData from '../components/users.json';
import SignUp from "../components/SignUp";
const Home = lazy(() => import("../pages/Home"));
const BrowsePage = lazy(() => import("../pages/BrowsePage"));

const RoutePaths = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user && user.loggedIn) {
      setLoggedIn(true);
      setUser(user);
    }
  }, []);

  const handleLogin = (userData) => {
    const users = JSON.parse(localStorage.getItem('users')) || [];
    const currentUser = users.find(user => user.email === userData.email && user.password === userData.password);
    if (currentUser) {
      localStorage.setItem('user', JSON.stringify({ ...currentUser, loggedIn: true }));
      setLoggedIn(true);
      setUser({ ...currentUser, loggedIn: true });
    } else {
      alert('Invalid email or password.');
    }
  }

  const handleSignUp = (userData) => {
    const users = JSON.parse(localStorage.getItem('users')) || [];
    const userExists = users.find(user => user.email === userData.email);
    if (userExists) {
      alert('User with this email already exists.');
    } else {
      localStorage.setItem('users', JSON.stringify([...users, userData]));
      alert('Account created successfully.');
    }
  }

  const handleLogout = () => {
    localStorage.removeItem('user');
    setLoggedIn(false);
    setUser(null);
  }

  return (
    <BrowserRouter>
      {/* <Suspense fallback={<Spinner />}> */}
        <Router>
        {/* <Route path="/">
          {loggedIn ? <Navigate to="/home" /> : <Auth handleLogin={handleLogin} />}
        </Route>
          <Route path="/FlyM/">
          {loggedIn ? <Home user={user} handleLogout={handleLogout} /> : <Navigate to="/" />}
          </Route>
          <Route path="/FlyM/browse-page" element={<BrowsePage />}></Route>
          <Route path="/page" element={<FlightTracker />}></Route>
          <Route path="/flight" element={<Admindash />}></Route>
        <Route path="/signup">
        {loggedIn ? <Navigate to="/home" /> : <SignUp handleSignUp={handleSignUp} />}
      </Route> */}
       <Route exact path="FlyM/">
        {loggedIn ? <Navigate to="/home" /> : <Auth handleLogin={handleLogin} />}
      </Route>
      <Route path="/signup">
        {loggedIn ? <Navigate to="/home" /> : <SignUp handleSignUp={handleSignUp} />}
      </Route>
      <Route path="/home">
        {loggedIn ? <Home user={user} handleLogout={handleLogout} /> : <Navigate to="/" />}
      </Route>
        </Router>
      {/* </Suspense> */}
    </BrowserRouter>
  );
};

export default RoutePaths;
