import { BrowserRouter, Route, Routes, Redirect  } from "react-router-dom";
import { lazy } from "react";
import { Suspense } from "react";
import Spinner from "./../Layout/Spinner";
import { Navigate } from "react-router-dom";
import FlightTracker from "../components/FlightTracker";
const Home = lazy(() => import("../pages/Home"));
const BrowsePage = lazy(() => import("../pages/BrowsePage"));
import SignIn from "../components/SignIn";
import SignUp from "../components/SignUp";
import React, { useState, useEffect } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';

firebase.initializeApp({
  // Your Firebase configuration
  apiKey: "your-api-key",
  authDomain: "your-auth-domain.firebaseapp.com",
  projectId: "your-project-id",
  storageBucket: "your-storage-bucket.appspot.com",
  messagingSenderId: "your-messaging-sender-id",
  appId: "your-app-id"
});

const RoutePaths = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = firebase.auth().onAuthStateChanged((user) => {
      setUser(user);
    });
    return unsubscribe;
  }, []);

  const handleSignOut = async () => {
    try {
      await firebase.auth().signOut();
      setUser(null);
    } catch (error) {
      console.log(error);
    }
  };


  return (
    <BrowserRouter>
      <Suspense fallback={<Spinner />}>
        <Routes>
        <Route path="/signup" component={SignUp} />
        <Route path="/signin" component={SignIn} />
        {user ? (
          <Route path="/browse-page" render={() => <div>{<BrowsePage />}<button onClick={handleSignOut}>Sign Out</button></div>} />
        ) : (
          <Redirect to="/signin" />
        )}
          <Route path="/" element={<Navigate to="/FlyM/" />}></Route>
          <Route path="/FlyM/" element={<Home />}></Route>

          <Route path="/page" element={<FlightTracker />}></Route>
        </Routes>
      </Suspense>
    </BrowserRouter>
  );
};

export default RoutePaths;
