import React, { useState, useEffect } from "react";

const BrowsePage = () => {
  const [flightNumber, setFlightNumber] = useState("");
  const [departureDate, setDepartureDate] = useState("");
  const [flightInfo, setFlightInfo] = useState({});
  const [bookingStatus, setBookingStatus] = useState("");

  const API_KEY = "deb48d2e96msh1042a56aa3afa71p1c4e5ajsnb5a15b5cd394";
  const API_URL = `https://community-british-airways-flight-info.p.rapidapi.com/flights;flightNumber=${flightNumber};scheduledDepartureDate=${departureDate}.json`;

  useEffect(() => {
    const fetchFlightInfo = async () => {
      const response = await fetch(API_URL, {
        method: "GET",
        headers: {
          "x-rapidapi-key": API_KEY,
          "x-rapidapi-host":
            "community-british-airways-flight-info.p.rapidapi.com",
        },
      });
      const data = await response.json();
      setFlightInfo(data);
    };

    if (flightNumber !== "" && departureDate !== "") {
      fetchFlightInfo();
    }
  }, [flightNumber, departureDate]);

  const handleBookFlight = () => {
    setBookingStatus("Booked");
  };

  const handleRescheduleFlight = () => {
    setBookingStatus("Rescheduled");
  };

  const handleCancelFlight = () => {
    if (bookingStatus === "Booked") {
      setBookingStatus("Cancelled");
    }
  };

  return (
    <div>
      <h2>Flight Booking</h2>
      <form>
        <label htmlFor="flightNumber">Flight Number:</label>
        <input
          type="text"
          id="flightNumber"
          value={flightNumber}
          onChange={(e) => setFlightNumber(e.target.value)}
        />
        <br />
        <label htmlFor="departureDate">Departure Date:</label>
        <input
          type="date"
          id="departureDate"
          value={departureDate}
          onChange={(e) => setDepartureDate(e.target.value)}
        />
      </form>
      <h3>Flight Information</h3>
      {flightInfo.flightNumber && (
        <div>
          <p>
            Flight Number: {flightInfo.flightNumber}
            <br />
            Departure Airport: {flightInfo.departureAirport}
            <br />
            Arrival Airport: {flightInfo.arrivalAirport}
            <br />
            Scheduled Departure Time: {flightInfo.scheduledDepartureTime}
            <br />
            Scheduled Arrival Time: {flightInfo.scheduledArrivalTime}
          </p>
          <div>
            <button onClick={handleBookFlight}>Book Flight</button>
            <button onClick={handleRescheduleFlight}>Reschedule Flight</button>
            {bookingStatus === "Booked" && (
              <button onClick={handleCancelFlight}>Cancel Flight</button>
            )}
          </div>
          <p>Booking Status: {bookingStatus}</p>
        </div>
      )}
    </div>
  );
};


export default BrowsePage;








