import React, { useState, useEffect } from 'react';
import { Button, Table } from 'react-bootstrap';
import { Link } from 'react-router-dom';

function BrowsePage() {
  const [flights, setFlights] = useState([]);

  useEffect(() => {
    fetch('https://api.openflights.org/data/airlines.json')
      .then(response => response.json())
      .then(data => setFlights(data));
  }, []);

  const handleBooking = (flight) => {
    // implement the code to book the flight
  };

  const handleCancellation = (flight) => {
    // implement the code to cancel the flight
  };

  return (
    <div>
      <h2>Flights Available</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Airline Name</th>
            <th>Flight Number</th>
            <th>Departure</th>
            <th>Arrival</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {flights.map((flight, index) => (
            <tr key={index}>
              <td>{flight.name}</td>
              <td>{flight.flight_number}</td>
              <td>{flight.departure}</td>
              <td>{flight.arrival}</td>
              <td>{flight.price}</td>
              <td>
                <Button variant="primary" onClick={() => handleBooking(flight)}>Book</Button>{' '}
                <Button variant="danger" onClick={() => handleCancellation(flight)}>Cancel</Button>{' '}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Link to="/admin">Admin Dashboard</Link>
    </div>
  );
}



export default BrowsePage;

