import React, { useState, useEffect } from 'react';


const BrowsePage = () => {
  const [flights, setFlights] = useState([]);

  const fetch = require('node-fetch');

const url = 'https://community-british-airways-flight-info.p.rapidapi.com/flights;flightNumber=%7BflightNumber%7D;scheduledDepartureDate=%7BscheduledDepartureDate%7D.json?flightNumber=011&scheduledDepartureDate=2014-06-01';

const options = {
  method: 'GET',
  headers: {
    'Client-Key': 'undefined',
    'X-RapidAPI-Key': 'deb48d2e96msh1042a56aa3afa71p1c4e5ajsnb5a15b5cd394',
    'X-RapidAPI-Host': 'community-british-airways-flight-info.p.rapidapi.com'
  }
};

fetch(url, options)
	.then(res => res.json())
	.then(json => console.log(json))
	.catch(err => console.error('error:' + err));

  const handleBookFlight = (flight) => {
    // Logic to book flight
  };

  const handleCancelFlight = (flight) => {
    // Logic to cancel flight
  };

  const handleRescheduleFlight = (flight) => {
    // Logic to reschedule flight
  };

  return (
    <div>
      <h1>Browse Flights</h1>
      {flights.map((flight) => (
        <div key={flight.id}>
          <p>Flight Number: {flight.flightNumber}</p>
          <p>Scheduled Departure: {flight.scheduledDeparture}</p>
          <button onClick={() => handleBookFlight(flight)}>Book Flight</button>
          <button onClick={() => handleCancelFlight(flight)}>Cancel Flight</button>
          <button onClick={() => handleRescheduleFlight(flight)}>Reschedule Flight</button>
        </div>
      ))}
    </div>
  );
};
export default BrowsePage;








