import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const Flight = ({ airline, departureTime, departureGateNumber, boardingLocation, price }) => {
  const [isBooked, setIsBooked] = useState(false);
  const [isCancelled, setIsCancelled] = useState(false);
  const [isRescheduled, setIsRescheduled] = useState(false);
  const [bookedDate, setBookedDate] = useState("");
  const [cancelledDate, setCancelledDate] = useState("");
  const [rescheduledDate, setRescheduledDate] = useState("");
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState("");
  const [rescheduledFlightDate, setRescheduledFlightDate] = useState("");

  const handleBook = () => {
    if (selectedPaymentMethod === "") {
      alert("Please select a payment method to continue booking.");
    } else {
      setIsBooked(true);
      setBookedDate(new Date().toLocaleString());
    }
  };

  const handleCancel = () => {
    if (isBooked) {
      const confirmCancel = window.confirm("Are you sure you want to cancel your booking? Your payment will be refunded.");
      if (confirmCancel) {
        setIsCancelled(true);
        setCancelledDate(new Date().toLocaleString());
      }
    }
  };

  const handleReschedule = () => {
    if (isBooked) {
      const newDate = window.prompt("Please select a new flight date:");
      if (newDate) {
        setIsRescheduled(true);
        setRescheduledDate(new Date().toLocaleString());
        setRescheduledFlightDate(newDate);
      }
    }
  };

  const handlePaymentMethodChange = (e) => {
    setSelectedPaymentMethod(e.target.value);
  };

  return (
    <div>
      <div className="flight">
        <div className="fligh">
          <h2 style={{ color: "#003459", borderBottom: "black" }}>{airline}</h2>
          <p style={{ color: "red" }}>Departure Time: {departureTime}</p>
          <p style={{ color: "black" }}>Boarding Location: {boardingLocation}</p>
          <p style={{ color: "#244D61" }}>Gate of Departure: {departureGateNumber}</p>
          <p>Price: {price}</p>
        </div>
        {!isBooked ? (
          <div>
            <div>
              <label htmlFor="mastercard">Master Card</label>
              <input type="radio" id="mastercard" name="paymentMethod" value="mastercard" onChange={handlePaymentMethodChange} />
            </div>
            <div>
              <label htmlFor="paypal">Paypal</label>
              <input type="radio" id="paypal" name="paymentMethod" value="paypal" onChange={handlePaymentMethodChange} />
            </div>
            <div>
              <label htmlFor="mpesa">Mpesa</label>
              <input type="radio" id="mpesa" name="paymentMethod" value="mpesa" onChange={handlePaymentMethodChange} />
            </div>
            <div>
              <label htmlFor="payoneer">Payoneer</label>
              <input type="radio" id="payoneer" name="paymentMethod" value="payoneer" onChange={handlePaymentMethodChange} />
            </div>
            <button className="flig" onClick={handleBook}>
              Book Now
            </button>
          </div>
        ) : (
          <div>
          <p style={{ color: "green" }}>Booked on: {bookedDate}</p>
          <button className="flig" onClick={handleCancel}>
            Cancel Booking
          </button>
          <button className="flig" onClick={handleReschedule}>
            Reschedule
          </button>
        </div>
      )}
      {isCancelled && (
        <div>
          <p style={{ color: "red" }}>Cancelled on: {cancelledDate}</p>
        </div>
      )}
      {isRescheduled && (
        <div>
          <p style={{ color: "orange" }}>Rescheduled on: {rescheduledDate}</p>
          <div>
            <label htmlFor="rescheduleDate">Select new flight date:</label>
            <DatePicker selected={new Date(rescheduledFlightDate)} onChange={(date) => setRescheduledFlightDate(date)} id="rescheduleDate" />
          </div>
        </div>
      )}
    </div>
  </div>
);
};
export default Flight;
